import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ProductCard from "./ProductCard";
import {useHistory} from "react-router";
import ReactPaginate from "react-paginate";
import ShoppingPage from "./ShoppingPage";


// const PER_PAGE = 4;
const Products = ({ products, 
  // stores, 
  selectedStore, setSelectedStore, toClothes, toElectronics, toTools, toHealth, toMusic, toAll, fromMain, setFromMain, selectedProduct, setSelectedProduct, isLoading,
// clothes, electronics, tools, health, music, all, 
handleSelect, search, setSearch, handleChange, display, toDisplay, setToDisplay, searchProducts, clearSearch}) => {

    // const [search, setSearch] = useState('')
    // const [toDisplay, setToDisplay] = useState([])
    const [sortBy, setSortBy] = useState('')
    const [random, setRandom] = useState([])
      const [clothes, setClothes] = useState([])
  const [electronics, setElectronics] = useState([])
  const [tools, setTools] = useState([])
  const [health, setHealth] = useState([])
  const [music, setMusic] = useState([])
  const [stores, setStores] = useState([])
  const [all, setAll] = useState([])

    useEffect(() => {
      fetch("/stores").then((r) => {
        if (r.ok) {
          r.json().then((stores) => setStores(stores))
          .then(setClothes(stores[0]))
          .then(console.log(clothes))
          setElectronics(stores[1]);
          setTools(stores[2]);
          setHealth(stores[3]);
          setMusic(stores[4]);
          setAll(stores[5]);
        }
      });
    }, []);

function main(){
    setFromMain(false)
    // console.log(fromMain)
}

useEffect(() => {
    if(sortBy === 'Alphabetically'){
     const sortedProducts = sortByName()
     setToDisplay(sortedProducts)
    }else{
      const sortedProducts = sortByPrice()
      setToDisplay(sortedProducts)
    }
  }, [sortBy])

  const sortByName = () => {
    return[...toDisplay].sort(function(a, b) {
      var nameA = a.name.toUpperCase(); // ignore upper and lowercase
      var nameB = b.name.toUpperCase(); // ignore upper and lowercase
      if (nameA < nameB) {
        return -1;
      }
      if (nameA > nameB) {
        return 1;
      }
      // names must be equal
      return 0;
    });
  }

  const sortByPrice = () => {
    return[...toDisplay].sort(function (a, b) {
      return a.price - b.price;
    });
  }

  const sortProducts = (e) => {
    setSortBy(e.target.value)
  }

  const shuffled = products.sort(() => 0.5 - Math.random());

// Get sub-array of first n elements after shuffled
let selected = shuffled.slice(0, 3);
// console.log(selected)


  return (
    <div>


      <Container2>
            <br></br>
             {toDisplay.length > 0 ?
          <div>
            <button onClick={clearSearch}>Clear Search</button>
    <Container>
      <br></br>
        <Card1 key={clothes.id} onClick={() => {toClothes(); main()}}>
          <h2>{clothes.name.toUpperCase()}</h2>
        </Card1>
      <br></br>
        <Card2 key={electronics.id} onClick={() => {toElectronics(); main()}}>
          <h2>{electronics.name.toUpperCase()}</h2>
        </Card2>
      <br></br>
        <Card3 key={tools.id} onClick={() => {toTools(); main()}}>
          <h2 className="font-link">{tools.name.toUpperCase()}</h2>
        </Card3>
      <br></br>
        <Card4 key={health.id} onClick={() => {toHealth(); main()}}>
          <h2>{health.name.toUpperCase()}</h2>
        </Card4>
      <br></br>
        <Card5 key={music.id} onClick={() => {toMusic(); main()}}>
          <h2 className="guitar">{music.name}</h2>
        </Card5>
      <br></br>
        <Card6 key={all.id} onClick={() => {toAll(); main()}}>
          <h2>{all.name.toLowerCase()}</h2>
        </Card6>
    </Container>

    {toDisplay.length > 0 ? <h1>Browse Items Related to "{search}"</h1> : "Enter a Search or browse by Store" }
            <br></br>
          <div>
      <strong>Sort Products:</strong>
            <label>
              <input
